import React, { Component } from 'react';

export default class ContactsListItems extends Component {
  render() {
    const { name, id, number, deleteContact } = this.props;
    return (
      <li className="contact">
        <span>{name}</span>
        <span>{number}</span>
        <button type="button" id={id} onClick={deleteContact}>
          x
        </button>
      </li>
    );
  }
}

