import React, { Component } from "react";
// import { CSSTransition, TransitionGroup } from "react-transition-group";
import { connect } from "react-redux";
import contactActions from "../../redux/contact/contact-actions";
class ContactsListItems extends Component {
  render() {
    const {
      contact: { name, id, number },
      deleteContact,
    } = this.props;
    return (
      <li className="contact">
        <span>{name}</span>
        <span>{number}</span>
        <button
          className="deleteBtn"
          type="button"
          id={id}
          onClick={(e) => deleteContact(e.target.id)}
        >
          x
        </button>
      </li>
    );
  }
}
const mapStateToProps = (state) => ({
  contacts: state.contacts.items,
});
const mapDispatchToPRops = {
  deleteContact: contactActions.deleteContact,
};
export default connect(mapStateToProps, mapDispatchToPRops)(ContactsListItems);
