import React, { Component } from 'react';

export default class ContactForm extends Component {
  state = {
    value: '',
    tel: '',
  };

  render() {
    return (
      <section>
        <form className="form">
          <label>
            <input
              className="input"
              type="text"
              value={this.state.value}
              onClick={this.handleOnInput}
            ></input>
          </label>
          <label>
            <input
              className="input"
              type="tel"
              value={this.state.tel}
              onClick={this.handleOnInput}
            ></input>
          </label>
          <button type="submit" className="addContact-button">
            Add contact
          </button>
        </form>
      </section>
    );
  }
}
