import contactsActions from "../../redux/contact/contact-actions";
import axios from "axios";
const addContact = (contact) => (dispatch) => {
  dispatch(contactsActions.addContactRequest());
  fetch("http://localhost:2000/contacts");
  axios
    .post("http://localhost:2000/contacts", { contact })
    .then((response) => {
      console.log(response);
      dispatch(contactsActions.addContactSuccess());
    })
    .catch((error) => dispatch(contactsActions.addContactError()));
};
export default {
  addContact,
};
