import React, { Component } from 'react';
import { CSSTransition } from 'react-transition-group';

export default class ContactForm extends Component {
  state = {
    name: '',
    number: '',
    alertMessageShow: false,
  };
  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  handleSubmit = evt => {
    evt.preventDefault();

    const { name } = this.state;
    const { contacts } = this.props;
    const existedContact = contacts.find(contact => contact.name === name);
    if (existedContact) {
      this.setState({ alertMessageShow: true });
      // alert(`${name} is already in contacts`);
      // return
    }

    this.props.addContact(this.state.name, this.state.number);
    this.setState({ name: '', number: '' });
  };
  toggleAlert = () => {
    this.setState({ alertMessageShow: false });
  };
  render() {
    const { name, number, alertMessageShow } = this.state;

    return (
      <section className="wrapper">
        <CSSTransition
          in={alertMessageShow}
          timeout={250}
          classNames="alertFade"
          unmountOnExit
          // onEnter={() => this.setState({ alertMessageShow: false })}
          //onExited={() => this.setState({ alertMessageShow: false })}
        >
          <button className="alertMessage" onClick={this.toggleAlert}>
            <p>{name} is already in contacts</p>
          </button>
        </CSSTransition>
        <form onSubmit={this.handleSubmit} className="form">
          <label>
            Name
            <input
              className="input"
              type="text"
              name="name"
              value={name}
              onChange={this.handleChange}
              placeholder="Enter name"
            ></input>
          </label>
          <label>
            Number
            <input
              className="input"
              type="tel"
              value={number}
              onChange={this.handleChange}
              name="number"
              placeholder="Enter number"
            ></input>
          </label>
          <button type="submit" className="addContact-button">
            Add contact
          </button>
        </form>
      </section>
    );
  }
}
