//   const normalizedFilter = getFilter(state).toLowerCase();
//   return contacts.filter((contact) =>
//     contact.name.toLowerCase().includes(normalizedFilter)
//   );
// };
export default {
  getLoading,
  getFilter,
  getContacts,
  getContactById,
  getContactsFiltered,
};
