//   return contacts.filter((contact) =>
//     contact.name.toLowerCase().includes(normalizedFilter)
//   );
// };
//=============without createSelector=============
export default {
  getLoading,
  getFilter,
  getContacts,
  getContactById,
  getContactsFiltered,
};
